import React, { useState } from 'react';

function mySelect() {
  const [number, setNumber] = useState(0);

  const onIncrease = () => {
    setNumber(prevNumber => prevNumber + 1);
    console.log(number);

  }

  const onDecrease = () => {
    setNumber(prevNumber => prevNumber - 1);
    console.log(number);
  }

  return (
    <div>
      <h1>{number}</h1>
      <button onClick={onIncrease}>+1</button>
      <button onClick={onDecrease}>-1</button>
    </div>
  );
}

export default mySelect;
