    //     </Route>
      
    //     <Route path="/login">
    //       <Login/>
    //     </Route>

    //     <Route path="/signup">
    //       <Signup/>
    //     </Route>
        
    //   </Switch>
    // </Router>
    
  );
}

export default App;
