import React, { useRef, useState } from 'react';
import styles from './login.module.css';

const Login = (props) => {
    const [border, setBorder] = useState();
    const [focus, setForder] = useState(null);
    const emailRef = useRef();
    const passwordRef = useRef();
    
    const onFocus = (current) => {
        setBorder("0.2rem solid #1da1f2");
        setForder('email');
    }
    const onBlur = (current) => {
        setBorder("0.1rem solid #c4cfd6");
        setForder('password');
    }
    console.log(focus);
    return(
    <div className={styles.login}>
        <img className={styles.logo} src="/images/twitter.svg" alt="twitter_logo"/>
        <h1 className={styles.text}>트위터 로그인</h1>
        <div className={styles.input_field}>
            <label className={styles.input_label_email}
            style ={{border:border}}>
                <span className={styles.input_text}>이메일</span>
                    <input ref={emailRef} type="text" onFocus={onFocus} onBlur={onBlur}/> 
                </label>
            <label className={styles.input_label_password} style ={focus==="emai;" ? {border:border}:{border:border}}>
                <span className={styles.input_text}>비밀번호</span>
                <input  ref={passwordRef} type="password" onFocus={onFocus} onBlur={onBlur}/>
            </label>
            <button type="submit">로그인</button>
            <div>
                <span>비밀번호를 잊으셨나요?</span>
                <span>.</span>
                <span>트위터 가입</span>
            </div>
        </div>
    </div>
    )
};  

export default Login;
