import React from 'react';
// import styles from './info_check.module.css';
import styles from './info_input.module.css';


const Info_check = (props) => {
    
    const space = () =>{
        const birthday = props.birthday.splite('.');
        console.log(birthday.join('. '));
        return birthday.join('. ');
        
    }

    const bakcClick = () =>{
        props.setState('input');
    }
    
    return(
            <div className={styles.background}>
                <div className={styles.sinup}>
                    <form action="" >
                        <div className={styles.title}>
                            <div className={styles.Btn_bg}>
                            <img className={styles.back_Btn} onClic={bakcClick} src="/images/left-arrow.svg" alt=""/> 
                            </div>
                            <img className={styles.logo} src="/images/twitter.svg" alt=""/>
                            <button className={styles.next_Btn}  type='submit'>다음</button>
                        </div>
                        <div className={styles.body}>
                            <h1>계정을 생성하세요</h1>
                            <label className={styles.input_label_email}>
                                <span className={styles.input_text}>이름</span>
                                <input className ={styles.name_input} value={props.name} readOnly type="text"/> 
                            </label>
                            <label className={styles.input_label_password}>
                                <span className={styles.input_text}>이메일</span>
                                <input className ={styles.email_input} value={props.email} readOnly  type="email"/> 
                            </label>
                            <label className={styles.input_label_password}>
                                <span className={styles.input_text}>생년월일</span>
                                <input className ={styles.email_input} value={props.birthday} readOnly type="email"/> 
                            </label>
                            <h2>생년월일</h2>
                                <span className={styles.comment}>이 정보는 공개적으로 표시되지 않습니다. 비즈니스, 반려동물 등 계정 주제에 상관 없이 나의 연령을 확인하세요.</span>
                        </div>
                    </form>                    
                </div>
            </div>
    )
};

export default Info_check;
