import React, { useState, useRef } from 'react';
import styles from './signup.module.css';


const Signup = (props) =>{ 
    const emailRef = useRef();
    const nameRef = useRef();
    const [month, setMonth] = useState();
    const [date, setDate] = useState();
    const [year, setYear] = useState();
    




    const handleInput = () => {
        const email = emailRef.current.value.length;
    }


    const ref = (event, target) => {
        switch(target) {
            case 'Month':
                setMonth(event.target.value);
                break;

            case 'Date':
                setDate(event.target.value);
                break;

            case 'Year':
                setYear(event.target.value);
                break;
            default :
                setDate(1);
                setMonth(1);
                setYear(new Date().getFullYear());
        }     
           
    }






    const getMonth = () => {
        const array =  [];
        const start = 1;
        const end = 12;
        for(let i = start; i <= end; ++i) {
            array.push(<option value={i}>{`${i}월`}</option>);
        }
        return array;
    }

    const getDate = () => {
        const array =  [];
        const start = 1;
        const end = 31;
        for(let i = start; i <= end; ++i) {
            array.push(<option value={i}>{i}</option>);
        }
        return array;
    }
    const getYear = () => {
        const yearArray =  [];
        const startYear = 1900;
        const endYear = new Date().getFullYear();

        for(let i = endYear; i>= startYear; --i) {
            yearArray.push(<option value={i}>{i}</option>);
        }
        return yearArray;
    }
    

    return(
        <>
            <div className={styles.background}>
                <div className={styles.sinup}>
                    <img className ={styles.logo} src="/images/twitter.svg" alt=""/>
                    <button className ={styles.next_Btn}>다음</button>

                    <form action="">
                        <span>계정을 생성하세요</span>
                        <label className={styles.input_label_email}>
                            <span className={styles.input_text}>이름</span>
                            <input className ={styles.email_input} ref={nameRef} type="text" onChange={handleInput}/> 
                        </label>
                        <label className={styles.input_label_email}>
                            <span className={styles.input_text}>이메일</span>
                            <input className ={styles.email_input} ref={emailRef} type="email" onChange={handleInput}/> 
                        </label>
                        <span>생년월일</span>
                        <span>이 정보는 공개적으로 표시되지 않습니다. 비즈니스, 반려동물 등 계정 주제에 상관 없이 나의 연령을 확인하세요.</span>
                        
                        <select name="" id=" "  onChange={(e) => {ref(e,'Month')}}>
                            <option value="" selected disabled > </option>
                            {getMonth()}
                        </select>
                        <select name="" id="">
                            <option value="" selected disabled> </option>
                            {getDate()}
                        </select>
                        <select name="" id="">
                            <option value="" selected disabled> </option>
                            {getYear()}
                        </select>
                        
                        
                        
                        
                    </form>
                </div>
            </div>
        </>
    )
};

export default Signup;
