        const email = emailRef.current.value.length;
        const name = nameRef.current.value.length;
        email > 0 && 
        name > 0 &&
        month &&
        date &&
        year ? setDisable(false) : setDisable(true)
    }

    const getMonth = () => {
        const array =  [];
