class UserAPI {
    constructor(path, method = 'GET') {
        // this.path  = path;
        this.getRequestOptions = {
            method: `${method}`,
            redirect: 'follow'
        };
        this.main_path =  'http://localhost:8000/user/'
    }

    async getUsers() {
        const response = await fetch(`${this.main_path}${this.path}`, this.getRequestOptions);
        const result = await response.json();
        return result.items;
    }
}
