import React, { useState, useRef } from 'react';
import styles from './signup.module.css';


const Signup = (props) =>{ 
    const emailRef = useRef();
    const monthRef = useRef();
    const handleInput = () => {
        const email = emailRef.current.value.length;
    }

    const getMonth = () => {
        const array =  [];
        const start = 1;
        const end = 12;
        for(let i = start; i <= end; ++i) {
            array.push(<option value={`${i}월`}>{`${i}월`}</option>);
        }
        return array;
    }

    const getDate = () => {
        const array =  [];
        const start = 1;
        const end = 31;
        for(let i = start; i <= end; ++i) {
            array.push(<option value={i}>{i}</option>);
        }
        return array;
    }
    const getYear = () => {
        const yearArray =  [];
        const startYear = 1900;
        const endYear = new Date().getFullYear();

        for(let i = endYear; i>= startYear; --i) {
            yearArray.push(<option value={i}>{i}</option>);
        }
        return yearArray;
    }
    
    
    return(
        <>
            <div className={styles.background}>
                <div className={styles.sinup}>
                    <img className ={styles.logo} src="/images/twitter.svg" alt=""/>
                    <button className ={styles.next_Btn}>다음</button>

                    <form action="">
                        <span>계정을 생성하세요</span>
                        <label className={styles.input_label_email}>
                            <span className={styles.input_text}>이름</span>
                            <input className ={styles.email_input} ref={emailRef} type="text" onChange={handleInput}/> 
                        </label>
                        <label className={styles.input_label_email}>
                            <span className={styles.input_text}>이메일</span>
                            <input className ={styles.email_input} ref={emailRef} type="email" onChange={handleInput}/> 
                        </label>
                        <span>생년월일</span>
                        <span>이 정보는 공개적으로 표시되지 않습니다. 비즈니스, 반려동물 등 계정 주제에 상관 없이 나의 연령을 확인하세요.</span>
                        
                        <select name="" id="">
                            < option value="" selected disabled hidden > < /option>
                            {getMonth()}
                        </select>
                        <select name="" id="">
                            {getDate()}
                        </select>
                        <select name="" id="">
                            {getYear()}
                        </select>
                        
                        {/* {console.log(monthRef.current.value)} */}
                        
                        
                        
                        
                        
                        {/* <label className={styles.input_label_email}>
                            <span className={styles.input_text}>월</span>
                            <input className ={styles.email_input}ref={emailRef} type="month" onChange={handleInput}/> 
                        </label>

                        <label className={styles.input_label_email}>
                            <span className={styles.input_text}>일</span>
                            <input className ={styles.email_input}ref={emailRef} type="email" onChange={handleInput}/> 
                        </label>

                        <label className={styles.input_label_email}>
                            <span className={styles.input_text}>년</span>
                            <input className ={styles.email_input}ref={emailRef} type="date" onChange={handleInput}/> 
                        </label> */}
                    </form>
                </div>
            </div>
        </>
    )
};

export default Signup;
